package com.first;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int m1=900;
		int m2=1000;
		System.out.println("======ARITHMATIC OPERATION======");
		System.out.println(m1+m2);
		System.out.println(m1-m2);
		System.out.println(m1*m2);
		System.out.println(m1/m2);
		System.out.println(m1%m2);

	}

}
